document.getElementById("burgerMenu").addEventListener("click",(e=>{document.getElementById("sidebar").style.width="100vw"})),document.getElementById("exitSidebar").addEventListener("click",(e=>{document.getElementById("sidebar").style.width="0"})),document.querySelectorAll("input, textarea").forEach((e=>{e.addEventListener("invalid",(e=>{""===e.target.value?e.target.setCustomValidity("Debe ingresar un valor correcto."):e.target.setCustomValidity("")})),e.addEventListener("input",(e=>{e.target.setCustomValidity(""),e.target.checkValidity()}))})),document.querySelectorAll("input[type=checkbox]").forEach((e=>{e.addEventListener("invalid",(e=>{e.target.checked?e.target.setCustomValidity(""):e.target.setCustomValidity("Debe aceptar las condiciones de servicio")}))})),document.getElementById("contactForm").addEventListener("submit",(e=>{e.preventDefault(),location.replace("./confirmation.html")}));